(0x11e86f154212ef249e9e0205857feb80, 0x31000000000000000000000000000000, 'Relation Tree', 'RELATION_TREE_VIEW', '(\nSELECT \n    rt.*,\n    CONCAT(rt.relation_alias,rt.relation_alias_modifier) AS relation_alias_full,\n    IF (rt.unambiguous_relation_flag = 0 AND rt.only_required_reverse_relation_flag = 0, CONCAT(rt.relation_alias, rt.relation_alias_modifier), rt.relation_alias) AS relation_alias_short,\n    IF (rt.unambiguous_relation_flag = 0 AND rt.only_required_reverse_relation_flag = 0, CONCAT(rt.relation_name, \' (\', rt.attribute_name, \')\'), rt.relation_name) AS relation_name_short\nFROM (\n    SELECT \n      	a.oid, \n      	a.created_on, \n      	a.modified_on, \n      	a.created_by_user_oid, \n      	a.modified_by_user_oid, \n    	a.attribute_name,\n      	a.object_oid, \n      	a.related_object_oid, \n      	a.related_object_special_key_attribute_oid, \n        IF(\n          a.object_oid = [#LEFT_OBJECT#], \n          \'regular\', \'reverse\'\n        ) as relation_type, \n        IF(\n          a.object_oid = [#LEFT_OBJECT#], \n          a.related_object_oid, a.object_oid\n        ) as right_object_oid, \n        [#LEFT_OBJECT#] as left_object_oid, \n        IF(\n          a.related_object_oid = [#LEFT_OBJECT#], \n          ao.object_alias, a.attribute_alias\n        ) as relation_alias, \n        IF(\n          a.related_object_oid = [#LEFT_OBJECT#], \n          ao.object_name, a.attribute_name\n        ) as relation_name, \n        IF(\n          a.related_object_oid = [#LEFT_OBJECT#], \n          CONCAT(\'[\', a.attribute_alias, \']\'), \'\'\n        ) as relation_alias_modifier,\n        IF(\n            a.related_object_oid = [#LEFT_OBJECT#] AND (SELECT COUNT(*) FROM exf_attribute WHERE related_object_oid = [#LEFT_OBJECT#] AND object_oid = a.object_oid AND oid <> a.oid) > 0, \n            0, 1\n        ) as unambiguous_relation_flag,\n    	IF(\n            a.related_object_oid = [#LEFT_OBJECT#] AND a.attribute_required_flag = 1 AND (SELECT COUNT(*) FROM exf_attribute WHERE related_object_oid = [#LEFT_OBJECT#] AND object_oid = a.object_oid AND oid <> a.oid AND attribute_required_flag = 1) = 0, \n            1, 0\n        ) as only_required_reverse_relation_flag\n    FROM \n      	exf_attribute a \n    		LEFT JOIN exf_object ao ON ao.oid = a.object_oid\n    		LEFT JOIN exf_object ro ON ro.oid = a.related_object_oid\n    WHERE \n      	a.related_object_oid IS NOT NULL \n      	AND (\n        	a.object_oid = [#LEFT_OBJECT#] \n        	OR a.related_object_oid = [#LEFT_OBJECT#]\n      	)\n  	) rt\n)', NULL, 1, 0, 0x32000000000000000000000000000000, NULL, '', '', NULL, '', '2018-06-13 14:22:59', '2018-06-18 04:37:17', 0x31000000000000000000000000000000, 0x31000000000000000000000000000000),
